func detailScript(projectID int, templateID int, csrfToken string) templ.ComponentScript {
	return templ.ComponentScript{
		Name: `__templ_detailScript_baa6`,
		Function: `function __templ_detailScript_baa6(projectID, templateID, csrfToken){var _csrfToken = csrfToken;

	window.showDCTab = function(type) {
		document.getElementById('dc-tab-transit').classList.add('hidden');
		document.getElementById('dc-tab-official').classList.add('hidden');
		document.getElementById('tab-transit').classList.remove('border-brand-500', 'text-brand-600');
		document.getElementById('tab-transit').classList.add('border-transparent', 'text-gray-500');
		document.getElementById('tab-official').classList.remove('border-brand-500', 'text-brand-600');
		document.getElementById('tab-official').classList.add('border-transparent', 'text-gray-500');

		document.getElementById('dc-tab-' + type).classList.remove('hidden');
		document.getElementById('tab-' + type).classList.remove('border-transparent', 'text-gray-500');
		document.getElementById('tab-' + type).classList.add('border-brand-500', 'text-brand-600');
	};

	window.editTemplateFromDetail = function() {
		var el = document.getElementById('template-slideover');
		el.classList.remove('hidden');
		htmx.ajax('GET', '/projects/' + projectID + '/templates/' + templateID + '/edit', {target: '#template-form-container', swap: 'innerHTML'});
	};

	window.duplicateTemplate = function() {
		fetch('/projects/' + projectID + '/templates/' + templateID + '/duplicate', {
			method: 'POST',
			headers: {
				'X-CSRF-Token': _csrfToken,
				'Content-Type': 'application/json'
			}
		}).then(function(resp) {
			return resp.json().then(function(data) {
				if (resp.ok && data.success) {
					window.location.href = '/projects/' + projectID + '/templates/' + data.id;
				} else {
					showToast(data.error || 'Failed to duplicate template', 'error');
				}
			});
		}).catch(function() {
			showToast('Failed to duplicate template', 'error');
		});
	};

	window.closeTemplateSlideOver = function() {
		document.getElementById('template-slideover').classList.add('hidden');
	};

	window.confirmDeleteTemplate = function(projID, tplID, name) {
		document.getElementById('delete-template-name').textContent = name;
		document.getElementById('template-delete-modal').classList.remove('hidden');
		document.getElementById('confirm-delete-template-btn').onclick = function() {
			fetch('/projects/' + projID + '/templates/' + tplID, {
				method: 'DELETE',
				headers: {
					'X-CSRF-Token': _csrfToken,
					'Content-Type': 'application/json'
				}
			}).then(function(resp) {
				return resp.json().then(function(data) {
					closeDeleteTemplateModal();
					if (resp.ok && data.success) {
						window.location.href = '/projects/' + projID + '/templates';
					} else {
						showToast(data.error || 'Failed to delete template', 'error');
					}
				});
			}).catch(function() {
				closeDeleteTemplateModal();
				showToast('Failed to delete template', 'error');
			});
		};
	};

	window.closeDeleteTemplateModal = function() {
		document.getElementById('template-delete-modal').classList.add('hidden');
	};

	document.body.addEventListener('templateChanged', function() {
		window.closeTemplateSlideOver();
		window.location.reload();
	});
}`,
		Call:       templ.SafeScript(`__templ_detailScript_baa6`, projectID, templateID, csrfToken),
		CallInline: templ.SafeScriptInline(`__templ_detailScript_baa6`, projectID, templateID, csrfToken),
	}
}

